mapper.update:
UPDATE reply
	SET reply_step = reply_step + 1
	WHERE reply_group = #{board_no}

UPDATE reply
	SET reply_like = reply_like + 1
	WHERE reply_no = #{reply_no}

UPDATE reply
	SET reply_like = reply_like - 1
	WHERE reply_no = #{reply_no}

UPDATE reply
	SET reply_dislike = reply_dislike - 1
	WHERE reply_no = #{reply_no}

UPDATE reply
	SET reply_dislike = reply_dislike + 1
	WHERE reply_no = #{reply_no}
