mapper.update:
UPDATE entire_reply
	SET e_reply_step = e_reply_step + 1
	WHERE e_reply_group = #{no}

UPDATE entire_reply
	SET e_reply_like = e_reply_like + 1
	WHERE e_reply_group = #{no} AND e_reply_no = #{e_reply_no}

UPDATE entire_reply
	SET e_reply_dislike = e_reply_dislike - 1
	WHERE e_reply_group = #{no} AND e_reply_no = #{e_reply_no}
